                            // border:"2px solid black",
                            // borderRadius:"30%"

                        }
                        console.log("divcolor",divColor);
                        return(
                            <div >
                                <Button style={divStyle}>

                                </Button>
                            </div>

                        )
                    }

                    )
                }

                </div>
            )
        }
        else{
            colorDiv=null;
        }


        return (
            <div >

                {colorDiv}
            </div>
        )
    }
}
