import React from 'react';
import { connect } from 'react-redux';
import ReactPaginate from 'react-paginate';
// import Pagination from 'react-js-pagination';

import {Container , Row, Col} from 'react-bootstrap'
import { Button } from 'react-bootstrap'

import Footer from './footer'
import HomeNavBar from './navbar'
import SideMenu from './SideMenu';
import ProductCard from './ProductCard';
import {getProducts}  from '../actions/displayProductAction'

let fetcheddata=null;
class Products extends React.Component{
     constructor(){
         super();
         this.state={
            offset: 0,
            data: [],
            perPage: 5,
            currentPage: 0
         }
     }

    componentWillMount(){
        this.props.getProducts().then(() => {
            const { productData } = this.props;
            console.log('in willmount',productData.productData);
            const y=productData.productData;
            console.log("y==",y);
            fetcheddata=y;
            console.log("fetcheddata",fetcheddata);
      })
    }
    receivedData() {
        console.log("in receivedata", fetcheddata);
        const slice = fetcheddata.slice(this.state.offset, this.state.offset + this.state.perPage)
                const postData = slice.map(pd => <React.Fragment>
                   

<ProductCard 
id={pd.product_id}
image={pd.product_image} 
title={pd.product_name} 
price={pd.product_cost} 
rating={pd.product_rating}
/>
                        
                            
                    
                    {/* <p>{pd.title}</p>
                    <img src={pd.thumbnailUrl} alt=""/> */}
                </React.Fragment>)

                this.setState({
                    pageCount: Math.ceil(fetcheddata.length / this.state.perPage),
                   
                    postData
                })
        
    }
    handlePageClick = (e) => {
        const selectedPage = e.selected;
        const offset = selectedPage * this.state.perPage;

        this.setState({
            currentPage: selectedPage,
            offset: offset
        }, () => {
            this.receivedData()
        });

    };
    render()
    {
        const { productData } = this.props;
        console.log('in render',productData.productData);
        const y=productData.productData;
        console.log("y==",y);
        fetcheddata=y;
        console.log("in render,fetcheddata==",fetcheddata);

        // const x=productData.productData;
        // console.log('next of render',x.product_details);
        return(
            <div>
                <HomeNavBar/>
                <hr></hr>
                   <div>
                       <Container>
                           <Row>
                               <Col xs={2}><SideMenu/></Col>
                               <Col>
                               <Row>
                                   <Col>
                                        <h2 className="header1">All Categories</h2>
                                   </Col>
                                   <Col>
                                        Sort By:
                                        <Button className="sort">★</Button>
                                        <Button className="sort">₹</Button>
                                        <Button className="sort">₹</Button>
                                   </Col>
                               </Row>
                               <Row>
                                   
                               <div className="popularProduct">
                                         
                                         

                                         {/* <div className="product_pagination">
					
				</div> */}
                <div>
                {this.state.postData}
                <ReactPaginate
                    previousLabel={"prev"}
                    nextLabel={"next"}
                    breakLabel={"..."}
                    breakClassName={"break-me"}
                    pageCount={this.state.pageCount}
                    marginPagesDisplayed={2}
                    pageRangeDisplayed={5}
                    onPageChange={this.handlePageClick}
                    containerClassName={"pagination"}
                    subContainerClassName={"pages pagination"}
                    activeClassName={"active"}/>
            </div>
                                         {/* <Pagination
                                    activePages = {this.state.activePages}
                                    itemsCountPerPage = {10}
                                    totalItemsCount = {450}
                                    pageRangeDisplayed = {5}
                                    onChange = {this.handlePageChange}
                                   ></Pagination> */}
                                     </div>
                               </Row>
                               
                               </Col>
                           </Row>
                       </Container>
                   </div>
                   <hr></hr>
                <Footer/>
            </div>
        );
    }
}

const mapStateToProps = state => ({
    productData: state.productData
  });

  const mapDispatchToProps = {
  getProducts
};
// const mapStateToProps = state => {
//     // console.log(state,state.productData.NoOfProducts,"hello");
//     return state
//     //     NoOfProducts : state.productData.NoOfProducts
//     // }
// }

    // const mapDispatchToProps = dispatch =>{
    //     return bindActionCreators({

    //         displaydata:()=>dispatch(displaydata())
    //     }
    //     )
    // }
// export default connect(mapStateToProps,mapDispatchToProps)(Products)
export default connect(
    mapStateToProps,
    mapDispatchToProps
  )(Products);
