import * as actionTypes from "../lib/constants/actionTypes";
import { BASE_URL } from "../constants/BaseURL";

import axios from "axios";

const getCartDataFromApiSuccess = (data) => ({
  type: actionTypes.GET_CART_DATA_FROM_API,
  payload: data,
});
export function getCartDataFromApi(token) {
  console.log("in action of getcartdata from api");
  return function (dispatch) {
    return axios
      .get(BASE_URL + "getCartData", {
        headers: { Authorization: `Bearer ${token}` },
      })
      .then(({ data }) => {
        console.log("response of getcart api", data);
        // const products = data.product_details;
        // console.log("products",products);
        dispatch(getCartDataFromApiSuccess(data));
      }).catch((error)=>{
          swal(`Error : ${error.message}`);
      })
  };
}
