import React from 'react'
import { Card } from 'react-bootstrap'
import {Link} from 'react-router-dom'
import '../assets/css/ProductCard.css'
import StarRatingComponent from 'react-star-rating-component';

export default class ProductCard extends React.Component{
    constructor(props)
    {
        super(props);
        this.state=
        {
            cartProduct:''
        }
    }
    handleCart=(e)=>
    {
        this.setState({cartProduct:this.cartProduct+1});
    }
    render()
    {
    return(
            <Card className="ProductCardwrapperCard">
                <Card.Img className="ProductCardwrapperImg" src={'http://180.149.241.208:3022/' + this.props.image}/> 
                <div className="ProductCardwrapperTitleWrapper">
                    <Link to={{
                        pathname : "/ProductDetails",
                        state:{
                                productId : this.props.id
                        }
                    }}>
                        <Card.Title className="ProductCardwrapperTitle">
                            {this.props.title}
                        </Card.Title>
                    </Link>
                </div> 
                <Card.Text className="productCart_price"> ₹ {this.props.price} </Card.Text>
                <button className="ProductCardwrapperButton" onClick={this.handleCart}>Add To Cart</button>
                <Card.Text className="productCart_rating">
                    <StarRatingComponent 
                        name="rate2" 
                        editing={false}
                        renderStarIcon={() => <span>★</span>}
                        starCount={5}
                        value={this.props.rating}
                    />
                </Card.Text>
            </Card>
    )
}
}
