import React from 'react'
// import axios from 'axios';
import { connect } from 'react-redux'
import {getProducts}  from '../actions/displayProductAction'
// import { bindActionCreators } from 'redux'


import {Container , Row, Col} from 'react-bootstrap'
import {Card} from 'react-bootstrap'

import Footer from './footer'
import HomeNavBar from './navbar'
import SideMenu from './SideMenu';



  class Products extends React.Component{
    
      constructor(props){
        super(props);
      }  
    //    componentWillMount(){
    //     axios.get(`http://180.149.241.208:3022/getAllProductsInDescending`)
    //     .then(res => {
    //         const product = res.data;
    //         this.setState({product});
    //         console.log(res,product);
    //     })
    //    }
    componentWillMount(){
        // console.log("in componentwillmount");
        this.props.getProducts().then(() => {
            const { productData } = this.props;
            // console.log('in willmount',productData);
      })
    }
    // componentDidMount(){
    //     console.log("in componentdidmount");
    // }
    render()
    {
        const { productData } = this.props;
        // use product data with map to view product
    
    
        return(
            <div>
                <HomeNavBar/>
                <hr></hr>
                   <div>
                       <Container>
                           <Row>
                               <Col xs={4}><SideMenu/></Col>
                               <Col>
                                    <div>
                                         <h2 className="header1">Products</h2>
                
                                        {
                                            Data.productData.map((productData,i)=>{
                                            return(
                                                <Card className="productCart">
                                                    <div key={i} className="productCart_body">
                                                     <Card.Img className="productCartImg" variant="top" src={Product.image} />
                                                     <Card.Body className="productCart_body_display">
                                                     <Card.Title className="productCart_title">{Product.title}</Card.Title>
                                                    <Card.Text className="productCart_price">{Product.prise}</Card.Text>
                                                    <button className="productCart_button">Add To Cart</button>
                                                    </Card.Body>
                                                    </div>
                                                </Card>
                                             );
                                            })
                                         }
                                     </div>
                               {/* <p>{ this.state.product.map(product => <li>{product.id}</li>)}</p> */}
                                {/* <h2>No of products-{this.props.data}</h2>
                                <button onClick={this.props.displaydata}>get products</button> */}
                               </Col>
                           </Row>
                       </Container>
                   </div>
                <Footer/>
            </div>
        );
    }
}

const mapStateToProps = state => ({
    productData: state.productData
  });

  const mapDispatchToProps = {
  getProducts
};
// const mapStateToProps = state => {
//     // console.log(state,state.productData.NoOfProducts,"hello");
//     return state
//     //     NoOfProducts : state.productData.NoOfProducts
//     // }
// }

    // const mapDispatchToProps = dispatch =>{
    //     return bindActionCreators({

    //         displaydata:()=>dispatch(displaydata())
    //     }
    //     )
    // }
// export default connect(mapStateToProps,mapDispatchToProps)(Products)
export default connect(
    mapStateToProps,
    mapDispatchToProps
  )(Products);
