import * as actionTypes from "../../lib/constants/actionTypes";

const initialState = {
  NoOfProducts:10
};

const productReducer = (state = initialState, action) => {
  switch (action.type) {
    case actionTypes.DISPLAYPRODUCTS:
      return {
        ...state,
        NoOfProducts : state.NoOfProducts -1
      };
    default:
        return {
            ...state,
            NoOfProducts : state.NoOfProducts -1
          };
  }
};

export default productReducer;
