import React from 'react'
import axios from 'axios';
import { connect } from 'react-redux'
import {displayProduct}  from '../actions/displayProductAction'
import { bindActionCreators } from 'redux'


import {Container , Row, Col} from 'react-bootstrap'

import Footer from './footer'
import HomeNavBar from './navbar'
import SideMenu from './SideMenu';



  class Products extends React.Component{
    
      constructor(props){
        super(props);
      }  
       componentWillMount(){
        axios.get(`http://180.149.241.208:3022/getAllProductsInDescending`)
        .then(res => {
            const product = res.data;
            this.setState({product});
            console.log(res,product);
        })
       }
    render()
    {
        return(
            <div>
                <HomeNavBar/>
                <hr></hr>
                   <div>
                       <Container>
                           <Row>
                               <Col xs={4}><SideMenu/></Col>
                               <Col>
                               {/* <p>{ this.state.product.map(product => <li>{product.id}</li>)}</p> */}
                                <h2>No of products-{this.props.NoOfProducts}</h2>
                                <button onClick={this.props.displayProduct}>get products</button>
                               </Col>
                           </Row>
                       </Container>
                   </div>
                <Footer/>
            </div>
        );
    }
}
