import React from "react";
import { connect } from "react-redux";
import ReactPaginate from "react-paginate";

import { Container, Row, Col } from "react-bootstrap";
import { Button } from "react-bootstrap";

import Footer from "./footer";
import HomeNavBar from "./navbar";
import SideMenu from "./SideMenu";
import ProductCard from "./ProductCard";
import { getProducts } from "../actions/displayProductAction";
import StarIcon from "@material-ui/icons/Star";
import ArrowUpwardIcon from "@material-ui/icons/ArrowUpward";
import ArrowDownwardIcon from "@material-ui/icons/ArrowDownward";
import { getCategories } from "../actions/ListOfSideMenuAction";

let fetcheddata = null;
class Products extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      offset: 0,
      data: [],
      perPage: 6,
      currentPage: 0
    };
  }

  componentWillMount() {
    this.props.getProducts().then(() => {
      const { productData } = this.props;
      const y = productData.productData;
      fetcheddata = y;
      //console.log("fetched data :::::",fetcheddata);
    });
  }

  receivedData() {
    if (fetcheddata != undefined) {
      const slice = fetcheddata.slice(
        this.state.offset,
        this.state.offset + this.state.perPage
      );
      const postData = slice.map(pd => (
        <React.Fragment>
          <ProductCard
            id={pd.product_id}
            image={pd.product_image}
            title={pd.product_name}
            price={pd.product_cost}
            rating={pd.product_rating}
          />
        </React.Fragment>
      ));
      this.setState({
        pageCount: Math.ceil(fetcheddata.length / this.state.perPage),
        postData
      });
    }
  }
  handlePageClick = e => {
    const selectedPage = e.selected;
    const offset = selectedPage * this.state.perPage;
    this.setState(
      {
        currentPage: selectedPage,
        offset: offset
      },
      () => {
        this.receivedData();
      }
    );
  };
  myCallback(fetchedData) {
    console.log("^^^^^^^^^in sidemenu of product page..", fetchedData);
  }
  render() {
    const { categories } = this.props;
    console.log("----------in product page and data is-------===", categories);
    const { productData } = this.props;
    const y = productData.productData;
    fetcheddata = y;
    let paginationLayout = "";
    if (fetcheddata != undefined) {
      paginationLayout = (
        <ReactPaginate
          previousLabel={"prev"}
          nextLabel={"next"}
          breakLabel={"..."}
          breakClassName={"break-me"}
          pageCount={this.state.pageCount}
          marginPagesDisplayed={2}
          pageRangeDisplayed={5}
          onPageChange={this.handlePageClick}
          containerClassName={"pagination"}
          subContainerClassName={"pages pagination"}
          activeClassName={"active"}
        />
      );
    } else {
      paginationLayout = (
        <h1 style={{ paddingLeft: "204px", paddingTop: "12px" }}>
          No products available
        </h1>
      );
    }
    return (
      <div>
        <HomeNavBar />

        <hr style={{ marginTop: "40px" }}></hr>
        <div>
          <Container>
            <Row>
              <Col xs={2}>
                  <SideMenu
                    callbackFromParent={(fetchedData)=>{this.myCallback()}}
                  >

                  </SideMenu>
                {/* <SideMenu
                  callbackFromParent={fetchedData => {
                    console.log("in callbackfromparent of propduct page");
                    return myCallback(fetchedData);
                  }}
                /> */}
              </Col>
              <Col style={{ marginLeft: "78px" }}>
                <Row>
                  <p className="header1">
                    <span style={{ fontSize: "25px" }}>All Categories</span>
                    <span style={{ paddingLeft: "370px", fontSize: "16px" }}>
                      Sort By:
                      <Button className="sort">
                        <StarIcon />
                      </Button>
                      <Button className="sort">
                        ₹<ArrowUpwardIcon></ArrowUpwardIcon>
                      </Button>
                      <Button className="sort">
                        ₹<ArrowDownwardIcon />
                      </Button>
                    </span>
                  </p>

                  <Col></Col>
                  <Col style={{ paddingLeft: "168px" }}></Col>
                </Row>
                <Row>
                  <div className="popularProduct">
                    <div>
                      {this.state.postData}

                      {paginationLayout}
                    </div>
                  </div>
                </Row>
              </Col>
            </Row>
          </Container>
        </div>
        <hr></hr>
        <Footer />
      </div>
    );
  }
}

const mapStateToProps = state => {
  if (state.categories != undefined) {
    console.log("%%%%%%%%%%  categories==", state.categories);
  }

  return {
    productData: state.productData,
    categories: state.categories
  };
};

const mapDispatchToProps = {
  getProducts,
  getCategories
};

export default connect(mapStateToProps, mapDispatchToProps)(Products);
