import React from 'react'

export default class AddToCart extends React.Component{
    constructor(){
        super();
        this.state = {

           cartProduct:0
         };
    }
    handleCart=(e)=>
    {
        console.log("cartProduct:::",this.state.cartProduct);
        this.state.cartProduct=(this.state.cartProduct)+1;
        console.log("cartProduct=====",this.state.cartProduct);
        // this.setState({cartProduct:this.cartProduct+1});
        // console.log("cart value===",this.cartProduct);
    }
    render(){
        return(

                <button 
                    className="ProductCardwrapperButton" 
                    style={{marginLeft:this.props.m,backgroundColor:this.props.color}}
                    onClick={this.handleCart}>
                    Add To Cart
                </button>

        );
    }
}
